//该文件用于创建Vuex中最为核心的store
//引入vue
import Vue from 'vue'

//引入Vuex
import Vuex from 'vuex'
Vue.use(Vuex)

//求和相关配置
const countOptions = {
    actions:{
        jianOdd(context, value){
            if(context.state.sum % 2){
                context.commit('JIA',value)
            }
        },
        jianWait(context, value){
            setTimeout(() => context.commit('JIAN', value),3000)
            // context.commit('JIAN',value)
        }
    },
    mutations:{
        JIA(state, value){
            console.log('mutations中JIA被调用了');
            state.sum += value
        },
        JIAN(state, value){
            console.log('mutations中JIA被调用了');
            state.sum -= value
        },
        JIANODD(state, value){
            console.log('mutations中JIA被调用了');
            state.sum += value
        },
        JIANWAIT(state, value){
            console.log('mutations中JIA被调用了');
            state.sum -= value
        },
        ADD_PERSON(state, value){
            console.log('mutations中JIA被调用了');
            state.personList.unshift(value)
        }
    },
    state:{
        sum:0,    //当前的和
        school:'尚硅谷',
        subject: '前端',
        personList:[
            {id: '001',name:'张三'},
        ]
    },
    getters:{
        bigSum(state){
            return state.sum * 10
        }
    }
}
const personOptions = {
    actions:{},
    mutations:{},
    state:{},
    getters:{}
}

//创建并暴露store
export default new Vuex.Store({
    modules: {
        a:countOptions,
        b:personOptions
    }
})
