//该文件用于创建Vuex中最为核心的store
//引入vue
import Vue from 'vue'

//引入Vuex
import Vuex from 'vuex'

//准备actions--用于响应组件中的动作
const actions = {
    jia(context, value){
        console.log('actions被调用了' , context, value)
        context.commit('JIA',value)
    }
}
//准备mutations--用于操作数据(state)
const mutations = {
    JIA(state, value){
        console.log('mutations中JIA被调用了', state,value);
        this.sum += value
    }
}
//准备state--用于存储数据
const state = {
    sum:0,    //当前的和

}
Vue.use(Vuex)
//创建并暴露store
export default new Vuex.Store({
    actions,
    mutations,
    state,
})
