import { mapState } from 'vuex';
export default {
  // eslint-disable-next-line vue/multi-word-component-names
  name: 'Count',
  data: function data() {
    return {
      n: 1 //用户选择的数字

    };
  },
  computed: {
    // 靠程序员自己亲自去写计算属性
    sum: function sum() {
      return this.$store.state.sum;
    },
    school: function school() {
      return this.$store.state.school;
    },
    subject: function subject() {
      return this.$store.state.subject;
    },
    //展开运算符  借助mapState生成计算属性, 从state中读取数据。（对象写法）
    // ...mapState({he:'sum',xuexiao:'school',xueke:'subject'}),

    /****************************************** */
    bigSum: function bigSum() {
      return this.$store.getters.bigSum;
    }
  },
  methods: {
    increment: function increment() {
      this.$store.commit('JIA', this.n);
    },
    decrement: function decrement() {
      this.$store.commit('JIAN', this.n);
    },
    incrementOdd: function incrementOdd() {
      this.$store.dispatch('jianOdd', this.n);
    },
    incrementWait: function incrementWait() {
      this.$store.dispatch('jianWait', this.n);
    }
  },
  mounted: function mounted() {
    var x = mapState({
      he: 'sum',
      xuexiao: 'school',
      xueke: 'subject'
    });
    console.log(x);
  }
};
