import axios from 'axios';
export default {
  // eslint-disable-next-line vue/multi-word-component-names
  name: 'Search',
  data: function data() {
    return {
      keyWord: ''
    };
  },
  methods: {
    searchUsers: function searchUsers() {
      var _this = this;

      //请求前更新List的数据
      this.$bus.$emit('updateListData', {
        isFirst: false,
        isLoading: true,
        errmsg: '',
        users: []
      });
      axios.get("https://api.github.com/search/users?q=".concat(this.keyWord)).then(function (response) {
        console.log('请求成功了'); // this.$bus.$emit('updateListData' ,response.data.items)
        // this.$bus.$emit('updateListData' ,false,false,'',response.data.items)
        //请求成功后更新List的数据

        _this.$bus.$emit('updateListData', {
          isLoading: false,
          errmsg: '',
          users: response.data.items
        });
      }, function (error) {
        //请求失败后更新List的数据
        console.log('请求失败了', error.message);

        _this.$bus.$emit('updateListData', {
          isLoading: false,
          errmsg: error.message,
          users: []
        });
      });
    }
  }
};
